import modules.sp_randomness_test as sp
from classes.Hadamar import Hadamar
from classes.CryptoSignal import CryptoSignal
from modules.arr_procedures import print_sig_in_list, derivativeSigFromTo, write_list_to_file
import  modules.fips_randomness_test as fp


# Only for testing FIPS140 creating large stream of bits
def write_ensemble_in_file(list_of_signals,filename):
    with open(filename, 'w') as out:
        for sig in list_of_signals:
            out.write("".join('1' if bit==1 else '0' for bit in sig))


# converting -1 to 0
def convert_sig(signal):
    return [1 if signal[i]==1 else 0 for i in range(0,len(signal))]

#
# As a callback function fips or nist sp test should been used
def test(list_of_signals,callback):
    for i in range(0,len(list_of_signals)):
        print("\nSignal {0}".format(i))
        tmp = convert_sig(list_of_signals[i])
        res, p_val = callback(tmp)
        print("Result: {0} P-value: {1}".format("Success" if res==True else "Fail", p_val))

# Running all defined nist sp test
def run_sp_test(dersig):
    print('===Monobit test===')
    test(dersig, sp.monobit_test)
    print('\n===Freq in block test===')
    test(dersig, sp.frequency_within_block_test)
    print('\n===Runs test===')
    test(dersig, sp.runs_test)
    print('\n===Longest run test===')
    test(dersig, sp.longest_run_ones_in_a_block_test)
    print('\n===Spectral test===')
    test(dersig, sp.dft_test)
    print('\n===Serial test===')
    test(dersig, sp.serial_test)

# Running all defined fips test
def run_fips_test(string_sig):
    print('===Monobit test===')
    res, x = fp.monobit_test(string_sig)
    print("Result: {0} X: {1}".format("Success" if res == True else "Fail", x))
    print('\n===Poker test===')
    res, x = fp.poker_test(string_sig)
    print("Result: {0} X: {1}".format("Success" if res == True else "Fail", x))
    print('\n===Run test===')
    res, arr,arr2 = fp.runs_test(string_sig)
    print("Result: {0} Run 1: {1} Run 0: {2}".format("Success" if res == True else "Fail", arr, arr2))
    print('\n===Long Run test===')
    res, x = fp.long_run(string_sig)
    print("Result: {0} X: {1}".format("Success" if res == True else "Fail", x))



